import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";
import Nav from "../Home/Nav/Nav";

function ProductDetail(){
    const p = useParams();
    console.log(p.productId);
    const [product,setproduct] =useState();

    useEffect(()=>{
        const url ='http://localhost:4000/getProduct/'+p.productId;
        axios.get(url)
        .then((res)=>{
            if(res.data.products){
                setproduct(res.data.products);
                console.log(res);
            }
            
        })
        .catch((err)=>{
            console.log(err);
            alert('server err');
        })

    },[]);
  const  HandleContact=(addedBy)=>{
        
    }

    return(<>
        <Nav></Nav>
            <h2>PRODUCT DETAILS:</h2>
             <div className="">
            
             {product && <div>
                    <div>
                        <img src={"http://localhost:4000/"+product.Pimage }alt="" />
                        <img src={"http://localhost:4000/"+product.Pimage2 }alt="" />
                        <h6>Product Description:</h6>
                        {product.ProductDesc}
                    </div>
                    <div>
                      {product.ProductName}
                      {product.ProductPrice}
                    </div>
                    <div>
                        {product.addedBy&& <button onClick={()=>HandleContact(product.addedBy)}>Show Contact Details:</button>}
                    </div>

                 </div>}
                 

        </div>
        </>
    );
}
export default ProductDetail;
