import { Link , Navigate ,useNavigate} from "react-router-dom";
import Home from "../../Home";
import "./Nav.css";
import logo from './logo.png';
import Headroom from 'react-headroom';
import { ToastContainer, toast } from 'react-toastify';
 import 'react-toastify/dist/ReactToastify.css';
 import LikedProduct from "../../LikedProduct/LikedProduct";
 import user from "./user.png"
 import { useState } from "react";


function Nav(props){
  const dropdownRef = useRef(null);
   const msg1 ="You are Logged Out";
   useEffect(() => {
    const handleOutsideClick = (event) => {
      // Close the dropdown if the click is outside the dropdown
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setShowDropdown(false);
      }
    };

    // Add event listener when component mounts
    document.addEventListener('click', handleOutsideClick);

    // Clean up the event listener when component unmounts
    return () => {
      document.removeEventListener('click', handleOutsideClick);
    };
  }, []);
   const [showDropdown, setShowDropdown] = useState(false);

   const toggleDropdown = () => {
     setShowDropdown(!showDropdown);
   };
 
   const handleOptionClick = (page) => {
     // Handle the navigation to the selected page
     console.log(`Navigating to ${page}`);
     // You can use React Router or any other navigation method here
   };
  
  const  navigate =useNavigate();
  const handelLogout =()=>{
    localStorage.removeItem('token');
    localStorage.removeItem('userId');
    
    navigate('/Login');
  }
  const sellHandler = ()=>{
    if(localStorage.getItem('token')){
      navigate("/Addproduct");
    }
    else{
      toast("login first to sell");
      navigate('/Login');
    }
  }
    return(
      
    <Headroom>
      <nav class="NavBar">

   <div class="bar">
    <span><Link to="/"><img src={logo} alt="" /></Link></span>
    <div className="search">

      <input type="text" name="" id="" value={props && props.search} onChange={(e)=> props.handlesearch && props.handlesearch(e.target.value)}/>

      <button className="search-btn" onClick={()=>props.handleClick && props.handleClick()}><i class="fa fa-search " id="sch"></i></button>
    </div>

    <ul class="link">
        <li><Link to="/">Home</Link></li>
        <li><a href="#aboutus">About Us</a></li>
        <li><Link to="/Ourteam">Our Team</Link></li>
        <li><button class="sell" on onClick={sellHandler}>Sell Your Product</button></li>
        
        {/* <li> <button onClick={handelLogout}>LogOut</button></li>
        <li><Link to="/like-product">Liked Product</Link></li>
        <li><Link to="/UserProfile">UserProfile</Link></li> */}
    </ul>
    <button class="login"><Link to="/Login">Login/Singup</Link></button>
    <div className="user-container" onClick={toggleDropdown}>
        
        <img src={user} alt="User" className="user-image" />
    </div>
</div>


</nav> 
{showDropdown && (
        <div className="dropdown1">
          <ul>
            <li onClick={() => handleOptionClick('home')}>Home</li>
            <li onClick={() => handleOptionClick('profile')}>Profile</li>
            <li onClick={() => handleOptionClick('settings')}>Settings</li>
            {/* Add more items as needed */}
          </ul>
        </div>
      )}
</Headroom>
      
);
}
export default Nav;

