import { useState } from "react";
import "./UserProfile.css"
import userprofile from "./userprofile.jpg"
import axios from "axios";


function UserProfile(){
    let userId =localStorage.getItem('userId');
    const[user,setUser]=useState();
   
    const url ='http://localhost:4000/getUser/'+userId;
        axios.get(url)
        .then((res)=>{
            if(res.data.user){
                setUser(res.data.user);
            }  
        })
        .catch((err)=>{
            console.log(err);
            alert('server err');
        })

    const [email,setemail] =useState(user.email);
    const [username,setusername] =useState(user.username);
   const [phno,setphnumber] =useState(user.phno);
   const [hno,sethostelno] =useState(user.hno);
   const[roll ,setroll]=useState(user.roll);
   const[year,setyear]=useState(user.year);
   const[branch ,setbranch]=useState(user.branch);
;
   useEffect(()=>{
        const url ='http://localhost:4000/getProduct/'+p.productId;
        axios.get(url)
        .then((res)=>{
            if(res.data.products){
                setproduct(res.data.products);
                console.log(res);
            }
            
        })
        .catch((err)=>{
            console.log(err);
            alert('server err');
        })

    },[]);


   const update=()=>{
    console.log({
        username,
        email,
        phno,
        hno,
        roll,
        year,
        branch
    });
    const url ='http://localhost:4000/UserProfile';
    const data ={ username,
        email,
        phno,
        hno,
        roll,
        year,
        branch ,
    userId};
    axios.post(url,data)
    .then((res)=>{
        console.log(res.data);
        if(res.data.message){
            alert(res.data.message);
            
        }

    })
    .catch((err)=>{
        console.log(err);
        alert("server err");
    })
   }


     

    return(
        <div className="div">
              <img src={userprofile} alt="" />
            <form action="">
          
                <input id="username"type="text" value={username} onChange={(e)=>{setusername(e.target.value)}}placeholder="Username"/>
                <br />
                
                <input id="email"type="text" value={email} onChange={(e)=>{setemail(e.target.value)}} placeholder="Enter Your E-Mail"/>
                <br />
               
                <input id="phno"type="number" value={phno} onChange={(e)=>{setphnumber(e.target.value)}} placeholder="Enter Your Ph.No."/>
                <br />
                <input type="number" id="hostel-no"  min="1" max="13"value={hno} onChange={(e)=>{sethostelno(e.target.value)}}placeholder="HostelNo."/>
                  <br />
                
                
                <input id="roll"type="text" value={roll} onChange={(e)=>{setroll(e.target.value)}}
                placeholder="Roll"/>
                <br />
               
                <input type="number" name="" id="" max={4} min={1} value={year} 
                onChange={(e)=>{setyear(e.target.value)}} placeholder="year" />
                <br />
                
                <input type="text"  id="branch" value={branch} onChange={(e)=>{setbranch(e.target.value)}} placeholder="Branch"/>
                <br />
          
            </form>
            <button onClick={update}>Update Your Profile:</button>

        </div>
    );
}

export default UserProfile;
