import Nav from "./Home/Nav";
import { Link } from "react-router-dom";
import { useState } from "react";
import axios from "axios";
function Login(){
    const [username,setusername] =useState('');
    const [password,setpassword] =useState('');

    const  handleApi = ()=>{
       
        const url ='http://localhost:4000/login';
        const data ={ username, password};
        axios.post(url,data)
        .then((res)=>{
            console.log(res.data);
            if(res.data.message){
                alert(res.data.message);
            }
    
        })
        .catch((err)=>{
            console.log(err);
            alert("server err");
        })
       }
       
    return(
        <div>
            <Nav></Nav>
            welcome to login page
            <div className="Login-container">
                   <label htmlFor="username" value={username} onChange={(e)=>{setusername(e.target.value)}}>USERNAME</label>
                   <input type="text" id="username"/>
                   <br />
                   <label htmlFor="password" value={password}  onChange={(e)=>{setpassword(e.target.value)}}>Password</label>
                   <input type="text" id="password" />
                   <br />
                   <button id="login"onClick={handleApi}>Login</button>
                   <br />
                   <Link to="/Singup">SingUp</Link>
            </div>

        </div>
    )
    
}


export default Login;
