import { useState, useEffect } from "react";
import "./UserProfile.css";
import userprofile from "./userprofile.jpg";
import axios from "axios";
import Nav from "../Home/Nav/Nav";

function UserProfile() {
  const userId = localStorage.getItem("userId");
  const [user, setUser] = useState({
    email: "",
    username: "",
    phno: "",
    hno: "",
    roll: "",
    year:"" ,
    branch: "",
  });

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const url = `http://localhost:4000/getUser/${userId}`;
        const response = await axios.get(url);
        if (response.data.user) {
          setUser(response.data.user);
        }
      } catch (error) {
        console.error(error);
        alert("Server error");
      }
    };

    fetchUserData();
  }, [userId]);

  const [email, setemail] = useState("");
  const [username, setusername] = useState("");
  const [phno, setphnumber] = useState(0);
  const [hno, sethostelno] = useState(0);
  const [roll, setroll] = useState("");
  const [year, setyear] = useState(0);
  const [branch, setbranch] = useState("");

  // Set initial values when user data is available
  useEffect(() => {
    setemail(user.email);
    setusername(user.username);
    setphnumber(user.phno);
    sethostelno(user.hno);
    setroll(user.roll);
    setyear(user.year);
    setbranch(user.branch);
  }, [user]);

  const update = () => {
    const url = "http://localhost:4000/UserProfile";
    const data = {
      username,
      email,
      phno,
      hno,
      roll,
      year,
      branch,
      userId,
    };

    axios
      .post(url, data)
      .then((res) => {
        console.log(res.data);
        if (res.data.message) {
          alert(res.data.message);
        }
      })
      .catch((err) => {
        console.log(err);
        alert("Server err");
      });
  };

  return (
    <>

    </>
    
  );
}

export default UserProfile;
