import { useState, useEffect } from "react";
import "./UserProfile.css";
import userprofile from "./userprofile.jpg";
import axios from "axios";

function UserProfile() {
  const userId = localStorage.getItem("userId");
  const [user, setUser] = useState({
    email: "",
    username: "",
    phno: 0,
    hno: 0,
    roll: "",
    year: 0,
    branch: "",
  });

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const url = `http://localhost:4000/getUser/${userId}`;
        const response = await axios.get(url);
        if (response.data.user) {
          setUser(response.data.user);
        }
      } catch (error) {
        console.error(error);
        alert("Server error");
      }
    };

    fetchUserData();
  }, [userId]);

  const [email, setemail] = useState("");
  const [username, setusername] = useState("");
  const [phno, setphnumber] = useState(0);
  const [hno, sethostelno] = useState(0);
  const [roll, setroll] = useState("");
  const [year, setyear] = useState(0);
  const [branch, setbranch] = useState("");

  // Set initial values when user data is available
  useEffect(() => {
    setemail(user.email);
    setusername(user.username);
    setphnumber(user.phno);
    sethostelno(user.hno);
    setroll(user.roll);
    setyear(user.year);
    setbranch(user.branch);
  }, [user]);

  const update = () => {
    const url = "http://localhost:4000/UserProfile";
    const data = {
      username,
      email,
      phno,
      hno,
      roll,
      year,
      branch,
      userId,
    };

    axios
      .post(url, data)
      .then((res) => {
        console.log(res.data);
        if (res.data.message) {
          alert(res.data.message);
        }
      })
      .catch((err) => {
        console.log(err);
        alert("Server err");
      });
  };

  return (
    <div className="div">
      <img src={userprofile} alt="" />
      <form action="">
        <input
          id="username"
          type="text"
          value={username}
          onChange={(e) => setusername(e.target.value)}
          placeholder="Username"
        />
        <br />
        <input
          id="email"
          type="text"
          value={email}
          onChange={(e) => setemail(e.target.value)}
          placeholder="Enter Your E-Mail"
        />
        <br />
        <input
          id="phno"
          type="number"
          value={phno}
          onChange={(e) => setphnumber(e.target.value)}
          placeholder="Enter Your Ph.No."
        />
        <br />
        <input
          type="number"
          id="hostel-no"
          min="1"
          max="13"
          value={hno}
          onChange={(e) => sethostelno(e.target.value)}
          placeholder="HostelNo."
        />
        <br />
        <input
          id="roll"
          type="text"
          value={roll}
          onChange={(e) => setroll(e.target.value)}
          placeholder="Roll"
        />
        <br />
        <input
          type="number"
          name=""
          id=""
          max={4}
          min={1}
          value={year}
          onChange={(e) => setyear(e.target.value)}
          placeholder="year"
        />
        <br />
        <input
          type="text"
          id="branch"
          value={branch}
          onChange={(e) => setbranch(e.target.value)}
          placeholder="Branch"
        />
        <br />
      </form>
      <button onClick={update}>Update Your Profile:</button>
    </div>
  );
}

export default UserProfile;
