import { useState } from "react";
import "./UserProfile.css"
import userprofile from "./userprofile.jpg"


function UserProfile(){
    const [email,setemail] =useState('');
    const [username,setusername] =useState('');
   const [phno,setphnumber] =useState('');
   const [hno,sethostelno] =useState('');
   const[roll ,setroll]=useState('');
   const[year,setyear]=useState('');
   const[branch ,setbranch]=useState('');

   const update=()=>{
    console.log({
        username,
        email,
        phno,
        hno,
        roll
    });
   }


     

    return(
        <div className="div">
              <img src={userprofile} alt="" />
            <form action="">
          
                <input id="username"type="text" value={username} onChange={(e)=>{setusername(e.target.value)}}placeholder="Username"/>
                <br />
                
                <input id="email"type="text" value={email} onChange={(e)=>{setemail(e.target.value)}} placeholder="Enter Your E-Mail"/>
                <br />
               
                <input id="phno"type="number" value={phno} onChange={(e)=>{setphnumber(e.target.value)}} placeholder="Enter Your Ph.No."/>
                <br />
                <input type="number" id="hostel-no"  min="1" max="13"value={hno} onChange={(e)=>{sethostelno(e.target.value)}}placeholder="HostelNo."/>
                  <br />
                
                
                <input id="roll"type="text" value={roll} onChange={(e)=>{setroll(e.target.value)}}
                placeholder="Roll"/>
                <br />
               
                <input type="number" name="" id="" max={4} min={1} value={year} 
                onChange={(e)=>{setyear(e.target.value)}} placeholder="year" />
                <br />
                
                <input type="text"  id="branch" value={roll} onChange={(e)=>{setbranch(e.target.value)}} placeholder="Branch"/>
                <br />
                <button onClick={update}>Update Your Profile:</button>
            </form>

        </div>
    );
}

export default UserProfile;
